mapper.select:
SELECT DISTINCT u.* FROM sys_user u LEFT JOIN sys_user_role ur on u.id = ur.user_id LEFT JOIN sys_user_depart ud on u.id = ud.user_id WHERE 1 = 1 AND u.del_flag = 0
AND u.username LIKE concat(concat('%',#{username}),'%')
and ur.role_id in
#{roleId}
and ud.dep_id in
#{depId}
and u.username in
#{name}

select * from sys_user where del_flag = 0

	and id in (select user_id from sys_user_depart where dep_id in

		#{id}

	)


	and username = #{username}

mapper.update:
delete from sys_user_role
where role_id in

	#{id}

delete from sys_role_permission
where role_id in

	#{id}

mapper.select:
select * from sys_user where del_flag = 0

	and id in (select user_id from sys_user_depart where dep_id in

		#{id}

	)


	and username != #{username}
